import sqlite3

def create_horo_tables(connect, cursor): 
    cursor.execute("""CREATE TABLE IF NOT EXISTS horo_head(
                   horo_id INTEGER primary key,
                   type TEXT,
                   text_id INTEGER
                   )""")
    connect.commit()

    cursor.execute("""CREATE TABLE IF NOT EXISTS horo_sign(
                   sign_id INTEGER,
                   horo_id INTEGER,
                   day_b TEXT,
                   day_e TEXT,
                   sign TEXT,
                   text_id INTEGER
                   PRIMARY KEY(sign_id,horo_id)                   
                   )""")
    connect.commit()
    
    cursor.execute("""CREATE TABLE IF NOT EXISTS horo_text(
                   text_id INTEGER primary key,
                   text TEXT
                   )""")
    connect.commit()    

def insert_horo_head(connect, cursor, horo_id, horo_type, text_id):
    cursor.execute("SELECT * FROM 'horo_head' WHERE 'horo_id' = ?", (horo_id,))
    row = cursor.fetchone() 
    if row is None:
        try:
            sqlite_insert = """INSERT INTO horo_head
                                  (horo_id, type, text_id)
                                  VALUES (?, ?, ?);"""
    
            data_tuple = (horo_id, horo_type, text_id)
            cursor.execute(sqlite_insert, data_tuple)
            connect.commit()
    
        except sqlite3.Error as error:
            print("Ошибка при работе с SQLite", error)

def insert_horo_text(connect, cursor, text_id, text):
    cursor.execute("SELECT * FROM 'horo_text' WHERE 'text_id' = ?", (text_id,))
    row = cursor.fetchone()    
    if row is None:    
        try:
            sqlite_insert = """INSERT INTO horo_text
                                  (text_id, text)
                                  VALUES (?, ?);"""
    
            data_tuple = (text_id, text)
            cursor.execute(sqlite_insert, data_tuple)
            connect.commit()
    
        except sqlite3.Error as error:
            print("Ошибка при работе с SQLite", error)

def insert_horo_sign(connect, cursor, sign_id, horo_id, date_begin, date_end, sign, text_id):
    cursor.execute("SELECT * FROM 'horo_sign' WHERE 'sign_id' = ?", (sign_id,))
    row = cursor.fetchone()    
    if row is None:     
        try:
            sqlite_insert = """INSERT INTO horo_sign
                                  (sign_id, horo_id, date_begin, date_end, sign, text_id)
                                  VALUES (?, ?, ?, ?, ?, ?);"""
    
            data_tuple = (sign_id, horo_id, date_begin, date_end, sign, text_id)
            cursor.execute(sqlite_insert, data_tuple)
            connect.commit()
    
        except sqlite3.Error as error:
            print("Ошибка при работе с SQLite", error)

# Объявление и инициализация объектов БД
# подключение к бд
connect = sqlite3.connect('bd.db')
cursor = connect.cursor()

create_horo_tables(connect, cursor)

